#include `를 포함한다.
  Vector 초기화
# 직접 할당 vector&lt;int&gt; vec = { -1,-1,-1 };  # 함수형 할당(3개의 원소, -1로 초기화) vector&lt;int&gt; vec(3,-1);  # fill 함수를 통한 초기화 vector&lt;int&gt; vec(3); fill(vec.begin(),vec.end(),-1);  # assign 함수를 통한 초기화 vector&lt;int&gt; vec; vec.assign(3,-1); for (int i = 0; i &lt; 3; i++) {  cout &lt;&lt; vec[i] &lt;&lt; &#34; &#34;; }  # 다차원 벡터 초기화 vector&lt;vector&lt;int&gt;&gt; vec(3, vector&lt;int&gt;(3,-1));   Vector 특정 원소의 Index
vector&lt;int&gt; vec = { 1,2,3,4,5,6,7 }; vector&lt;int&gt;::iterator iter = find(vec.begin(), vec.end(), 4); // 첨~끝, 찾고자하는 Item Value를 find 함수에 넣고 iter(반복자)로 int index = distance(vec.begin(), iter); // 첫 인덱스 부터 반복자 사이의 거리를 이용하여 index 찾기 cout &lt;&lt; index;   Vector 특정 값이 존재하는지 확인
# 1번 방법(Use Iter) vector&lt;int&gt; myvector = { 10,20,37,40,50,60,30 }; vector&lt;int&gt;::iterator it = find(myvector.begin(), myvector.end(), 30); if (it != myvector.end()){  cout &lt;&lt; &#34;Found!\n&#34;; }else{  cout &lt;&lt; &#34;Not Found!\n&#34;; }  # 2번 방법(Not use Iter) vector&lt;int&gt; myvector = { 10,20,37,40,50,60,30 }; if(find(myvector.begin(), myvector.end(), 30) != myvector.end()){  cout &lt;&lt; &#34;Found!\n&#34;; }else{  cout &lt;&lt; &#34;Not Found!\n&#34;; }   Vector 복사
vectorTarget.resize(vectorSource.size()); std::copy(vectorSource.begin(), vectorSource.end(), vectorTarget.begin());

A.cpp라는 클래스에서 포인터 변수를 선언 하고 B.cpp 내의 클래스 함수에서 인자로 넘겨 동적할당한 후 사용되게 하려면&hellip;
 아래는 내부 버퍼를 가지고 카피하는 방식  //A.h u_int8_t *m_pBuf;  //A.cpp rc = g_func.GetLog_0x03(m_pBuf); display_getlog_03h(m_pBuf); delete[] m_pBuf;  //B.cpp u_int32_t CFuncUtil::GetLog_0x02(u_int8_t *&amp;getlog_m_pBuf){ 	u_int8_t *pBuf = new u_int8_t[nBufferSize]; 	memset(pBuf, 0x0, nBufferSize); 		// copy to m_pBuf 	getlog_m_pBuf = new u_int8_t[nBufferSize]; 	memcpy(getlog_m_pBuf, pBuf, nBufferSize);  	// free pBuf 	delete[] pBuf; 	pBuf = NULL; 		return RETURN_SUCCESS;  }  아래는 외부 것 그대로 사용하는 방식  //A.h u_int8_t *m_pBuf;  //A.cpp rc = g_func.GetLog_0x03(m_pBuf); display_getlog_03h(m_pBuf); delete[] m_pBuf;  //B.cpp u_int32_t CFuncUtil::GetLog_0x02(u_int8_t *&amp;getlog_m_pBuf){ 	getlog_m_pBuf = new u_int8_t[nBufferSize]; 	memset(getlog_m_pBuf, 0x0, nBufferSize); 		return RETURN_SUCCESS; } C++ difference between ** and *&amp; in parameter passing - Stack Overflow)
u_int8_t *&amp;getlog_m_pBuf 이렇게 사용하는 방식을 reference to pointer-to-node 이라 하며, u_int8_t **getlog_m_pBuf 이렇게 사용하는 방식을 pointer-to-pointer-to-node 이라 한다.
